#ifdef GL_ES
precision mediump float;
#endif

#define PI 3.14159265359

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 st = fragCoord.xy/iResolution.xy;

    vec3 backgroundColor = vec3(1.0, 0.8, 0.79);

    vec2 d = vec2(0.5) - st;
    float r = length(d);

    float time = abs(mod(iTime, 10.0) - 5.0);

    float f = cos(atan(d.y, d.x) * time + iTime);

    backgroundColor += step(0.3 * f, r) * vec3(1.0);

    fragColor = vec4(backgroundColor, 1.0);
}
