float beiz(float u, float c1, float c2, float c3, float c4) {
  return pow(1.0 - u, 3.0) * c1 + 3.0 * u * pow(1.0 - u, 2.0) * c2 + 3.0 * pow(u, 2.0) * (1.0 - u) * c3 + pow(u, 3.0) * c4;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	float c1 = 0.2;
    float c2 = 0.33;
    float c3 = 0.66;
    float c4 = 1.0;

    vec4 col = texture(iChannel0, uv);

    float xd = (sin(iTime) + 1.) / 2.;

    if(xd>0.5){
    col.r = beiz(col.r, c1, c2, c3, c4);
    col.g = beiz(col.g, c1, c2, c3, c4);
    col.b = beiz(col.b, c1, c2, c3, c4);
    }

    fragColor = col; //vec4(col,1.0);


}
