void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //constants
    float pi     = 3.14159265359;
    float piHalf = 1.57079632679;

    //variables
    float zoom = 2.;
    float speed = 1.25; //rotation speed
    float radius = 1.;


    vec2 uv = (fragCoord *2. - iResolution.xy)/iResolution.y;

    uv *= zoom;  

    vec3 col = vec3(0.); //black space

    float cycle = fract(iTime * speed); // from 0. to 1.
    //float alpha = cycle * 2. * pi; // alpha in rad from 0 to 2*pi
    float alpha = cycle;


    vec4 new3D = vec4(uv.x, uv.y, 0., 1.);

    //alpha = piHalf;
    mat4x4 rotationMat = mat4x4( //rotate by alpha on y axis)
        cos(alpha), 0., -sin(alpha), 0., 
        0., 		1., 0., 		0., 
        sin(alpha), 0., cos(alpha), 0., 
        0., 		0., 0., 		1.); 

    vec4 rot3D = new3D * rotationMat;

    if(length(rot3D.xyz) < radius){
        col = vec3(length(rot3D.xyz));
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
