// golfing 522 chars https://shadertoy.com/view/WtlyWs

void mainImage(out vec4 O, vec2 u) {                                                \
    vec2 R = iResolution.xy, U = ( u+u - R ) / R.y,
	     z = U + O.wx;  U.x -= .5;
    U *= mat2(z,-z.y,z) / dot(U,U);
    z = U =   log( dot(U+=.5,U) ) / vec2(4,-2) + iTime/vec2(8,4)
            + atan(U.y,U.x)/6.2832 *vec2(6, 2);
    U = fract(U*1.5)*5.;
    int y = int(U.y) , x = ( int(U) + 3*y ) % 5;
    O = vec4( x<4 ? U = ( fract(U) + vec2(x%2,x<2) ) / 2.,
                   (x/2+y) % 5 : 5 ) *.1
      * min(1. - max(U= abs(fract(U)*2.-1.)-.95, U.y) / min(fwidth(z/.1),.3), 2.).x;
}
