void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 picsample = texture(iChannel1, uv.xy);

    uv.x *= iResolution.x / iResolution.y;

    // Time varying pixel color
    vec4 texsample = texture(iChannel0, uv.xy);


    float time01 = mod(iTime/10., 1.);
    float texsampleX = texsample.x + time01;

    float smoothstepped = smoothstep(0.9, 1., texsampleX);

    vec4 col = vec4(picsample.x * smoothstepped, picsample.y * smoothstepped, picsample.z * smoothstepped,1.);
    // Output to screen
    fragColor = col;
}
