void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord.xy-iResolution.xy)/iResolution.y;
    float circleDiv = .2;
    float tau = 3.1415926535*circleDiv;
    float a = atan(p.x,p.y);
    float r = length(p)*tan(iTime*0.5);
    vec2 uv = vec2(a/tau,r);
	float xCol = (uv.x - (iTime / 2.0)) * 1.0;
	xCol = mod(xCol, 1.0);
	vec3 stripes = vec3(0.4, .2, .3);	
    float circlesize = 1.0;
	uv = (2.0 * uv) - 1.0;
	vec3 horBeam = vec3(0.1*cos(uv.y*4.0)*clamp(floor( circlesize*sin(iTime*4.)),4.0, 10.0)*abs(10.0 / (40.0 * uv.y)));
	if (xCol < .5) 
    	{
		 stripes.r *= sin(iTime) - xCol;
         stripes.g *= cos(iTime)  - xCol;
		 stripes.b += xCol;
		}
    uv = p;
    //uv *= abs(uv);
  	float r2 = max( 0.,  sin(iTime)+3. - length(uv) );
    float t = (iTime*1.);
    t = r2 * r2 * cos(r2+t) * 5.;    
    uv *= mat2( cos(t), sin(t), cos(t), cos(t) );
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx);
    col *=0.4;
    vec3 horBeam2 = horBeam;
    horBeam *= smoothstep(0.,.5, vec3(uv.yx,horBeam.z));
    horBeam2 += smoothstep(0.,.5, vec3(uv.yx,horBeam.z));
    horBeam = mix(horBeam,horBeam2,0.3);
    fragColor = vec4((( horBeam) * stripes +col), 1.0);
}
