#define normal(c) normalize(c*2. - 1.)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int cellsize = 5;

    ivec2 cell = ivec2(floor(fragCoord.xy/float(cellsize)));
    ivec2 size = ivec2(textureSize(iChannel0, 0));

    vec3 sw = texelFetch(iChannel0, (cell + ivec2(0., 0.))%(size/cellsize), 0).xyz;
    vec3 nw = texelFetch(iChannel0, (cell + ivec2(0., 1.))%(size/cellsize), 0).xyz;
    vec3 ne = texelFetch(iChannel0, (cell + ivec2(1., 1.))%(size/cellsize), 0).xyz;
    vec3 se = texelFetch(iChannel0, (cell + ivec2(1., 0.))%(size/cellsize), 0).xyz;

    vec2 delta = fract(fragCoord.xy/float(cellsize));
    vec3 dirx1 = mix(sw, se, delta.x);
    vec3 dirx2 = mix(nw, ne, delta.x);
    vec3 dir = mix(dirx1, dirx2, delta.y);

    // visualize direction with diffuse shading
	vec3 vpos = vec3(0,0,0);
  	vpos.xy = fragCoord.xy / iResolution.y;
    vec3 vdir = normalize(vpos.xyz - vec3(0., 0., 1.)); 

    fragColor.xyz = dot(abs(normal(dir)), vdir) * vec3(1.,1.,0.);
}
