void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    fragColor.r = ( sin( 5.0f * (pow(uv.x - 0.3f, 2.0f)/pow(0.075f, 2.0f) + pow(uv.y - 0.5f, 2.0f)/pow(0.075f, 2.0f)) + iTime ) + sin( 5.0f * (pow(uv.x - 0.6f, 2.0f)/pow(0.075f, 2.0f) + pow(uv.y - 0.5f, 2.0f)/pow(0.075f, 2.0f)) + iTime ) ) / 2.0f;
    fragColor.b = 0.0f;
    fragColor.g = 0.0f;
}
