vec4 gammaCorrect(vec4 pixel)
{
    pixel = pow(pixel,vec4(2.2));
    return pixel;
}
vec3 trace(vec3 s, vec3 d)
{
    float h = s.y;
    float a = -d.y;
    float t = h/a;
    vec3 p = s+d*t;
    if(t > 0.)
        return vec3(gammaCorrect(texture(iChannel0, p.xz)));
    return vec3(gammaCorrect(texture(iChannel1, d)));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 yo = uv*2.-1.;
    vec3 dir = vec3(yo,sin(iTime*2.));
    vec3 camera = vec3(1,1,10. * iTime);
    vec3 col = vec3(trace(camera, dir));
    fragColor = vec4(pow(col,vec3(1./2.2)),1);
}
