void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{

    vec2 uv = (iResolution.xy - 2. * fragCoord.xy) / iResolution.y;
    int samp=0;
    float f = mainSound(  samp,-iTime).x;

    //vec3 color =uv.y + .5 * cos(tau * asin(uv.x) + vec3(0, f * 1.57, f * 3.14) + iTime);
    //vec3 color=uv.y +  vec3(f, f * 1.57, f * 3.14);
    vec3 color =uv.y + 0.25 * cos(tau * sin(uv.x) +  vec3(f) + iTime);

    fragColor.rgb = 1. - sqrt(color);

    // fragColor.rgb = vec3(mainSound( in int samp,iTime), 1) * 4.; // for debug
    fragColor.a = 1.; 
}

