float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

#define _num 200.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    int uvx = int(uv.x * _num);
    int uvy = int(uv.y * _num);
    uv.x = float(uvx) / _num;
    uv.y = float(uvy) / _num;
    float _rnd = rand(uv * iTime);
    _rnd = rand(vec2(_rnd, iTime));

    if(fract(iTime * 2.0) > 0.1)
        _rnd /= fract(iTime * 2.0);

    // Time varying pixel color
    vec4 col = vec4(_rnd, _rnd, _rnd, _rnd);

    // Output to screen
    fragColor = col;
}

