void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 xy = fragCoord.xy / iResolution.xy;//Condensing this into one line

   vec2 xy1 = fragCoord.xy/iResolution.xy;
   vec2 xy2 = fragCoord.xy/iResolution.xy;
   vec4 texColor = texture(iChannel0,xy2);

   xy1.x = xy1.x +0.05;
   vec4 texColor1 = texture(iChannel0,xy1);

   xy2.x = xy2.x - 0.05;
   vec4 texColor2 = texture(iChannel0, xy2);




   texColor1.r = texColor.b;
   texColor1.g = texColor.b;


   texColor2.b = texColor.r;
   texColor2.g = texColor.r;


   vec4 sum = (texColor+texColor2+texColor1)/2.0;
  //vec4 sum = texColor + texColor1 + texColor2;


   fragColor = sum;//Set the screen pixel to that color
}
