void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 minStep = 1.0 / iResolution.xy;
    vec2 uv = fragCoord / iResolution.xy;
    vec3 colC = texture(iChannel0, uv).rgb;
    vec3 colV = (
        texture(iChannel0, vec2(uv.x + minStep.x, uv.y)).rgb + 
        texture(iChannel0, vec2(uv.x - minStep.x, uv.y)).rgb +
        texture(iChannel0, vec2(uv.x, uv.y + minStep.y)).rgb +
        texture(iChannel0, vec2(uv.x, uv.y - minStep.y)).rgb) / 4.0;
    vec3 colB = colC - colV;
    float contrast = (colB.r + colB.g + colB.b) * 4.0/*Множителя контраста, чем больше, тем виднее*/;
    vec3 col = colC * contrast;
    fragColor = vec4(col,1.0);
}
