void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = -1.0 + 2.0*fragCoord.xy / iResolution.xy;
	uv.x *=  iResolution.x / iResolution.y;
     vec3 col =  vec3(0.502,0.502,0.502);


   	for( int i=0; i<400; i++ )
	{ 
    float siz = pow( sin(float(i)*641.74+5.0)*.5 + 0.5, 4.0 );

      float rad = 0.01 + .5*siz;
      uv += vec2(cos(iTime),sin(iTime))*rad;

        vec2 pos = vec2(rad*.001,rad*.001);
        float f = length(uv-pos)*5.;

       f = clamp(f,0.0,1.0);
       f = 1.-f;
       col-=f;


      }


fragColor = vec4(col,1.0);


}
