const float TAU = 6.28318530718;


vec2 Rotate(float a, vec2 p)
{
    return p * mat2(cos(a), sin(a), -sin(a), cos(a));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -.5 to .5)
    vec2 uv = fragCoord/iResolution.xy-.5;

    // Draw Background
    float a = fract(iTime * .1) * TAU;
    vec2 yelUV = Rotate(a, uv);
    float width = 0.1 * abs(sin(iTime));
    float height = 0.25 * abs(cos(iTime));
    float ew = 0.01;
    float c = .5 / max(width, 0.01);
    vec3 col = vec3(.5 / c);
    for(int i = 1; i < int(c); i++)
    {
        col.rg += 1. / (c+1.) * max(0.1, smoothstep(width * float(i), width * float(i) + ew, abs(yelUV.x)));
        col.b += 1. / (c+1.) * max(0.1, smoothstep(height * float(i), height * float(i) + ew, abs(yelUV.y)));
    }

    // Draw Dots
    int dotCount = 5;
    for(int i = 0; i < dotCount; i++)
    {
        float t = iTime - float(i) / float(dotCount) + float(i) * .075;
        float radius = 0.012 + 0.01 * sin(t * 1.75) - float(i) * 0.0001;
        vec2 offset = vec2(0.5 * sin(t * TAU / 3.), 0.3 * cos(t) + .1 * cos(t * TAU));
        vec2 uvc = vec2(uv.x + offset.x, uv.y - offset.y);
        float p = uvc.x * uvc.x + uvc.y * uvc.y * .5 * iResolution.y/iResolution.x;
        //ew += (float(i) / (float(dotCount) -  1.)) * 0.0009;
        float cir = smoothstep(radius, radius + ew, p);
        float val = .6 + radius * 20.;
        col = mix(vec3(val, uvc.y * 1.75, uvc.y * 1.75), col, cir);
    } 

    // Output to screen
    fragColor = vec4(col,1.0);
}
