void mainImage( out vec4 O,  vec2 u )
{
    vec2 R = iResolution.xy;
    u -= .5* R;

    O = vec4( cos( exp(15.*length(u/R.x)) - 10.*iTime  + vec4(0,1,2,0) ) );

 // if ( u.x > 0.) O /= fwidth(O);           // right: normalize to get B/W strips

    O = sqrt(.5+.5*O);                       // to sRGB

}
