vec2 random2( vec2 p ) {
    return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.yy;

    uv -= 0.5;
    float dist = distance(uv, vec2(0, 0));
    if (dist > 0.)
    {
        uv *= 1. / (pow(dist, 0.8));
    }
    uv += 0.5;

    uv *= 15.;

    vec2 i_uv = floor(uv);
    vec2 f_uv = fract(uv);

    float m_dist = 1.;

    for (int y= -1; y <= 1; y++) {
        for (int x= -1; x <= 1; x++) {
            vec2 neighbor = vec2(float(x),float(y));

            vec2 point = random2(i_uv + neighbor);

            vec2 diff = neighbor + point - f_uv;

            float distCell = length(diff);

            m_dist = min(m_dist, distCell);
        }
    }

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(/*iTime+*/i_uv.xyx+vec3(0,2,4));
    m_dist = pow(m_dist, 0.5);
    vec3 col = vec3(m_dist) * vec3(0.855, 0.569, 0);

    float outerRing = smoothstep(0.11, 0.15, dist);
    col = (1. - outerRing) * col + outerRing * (0.8 + 0.2 * m_dist) * mix(vec3(0.855, 0.569, 0), vec3(0.408, 0.627, 0.69), smoothstep(0.11, 0.2, dist));

    float borderStep = smoothstep(0.15, 0.2, dist);
    col = (1. - borderStep) * col + borderStep * vec3(1.);

    float pupilStep = smoothstep(0.07, 0.05, dist);
    col = (1. - pupilStep) * col + pupilStep * vec3(0.);


    // Output to screen
    fragColor = vec4(col,1.0);
}
