/// por jorge2017a1 ...jorge flores p. // 30-dic-2020

#define saturate(x) clamp(x, 0.0, 1.0)
#define R iResolution.xy
#define ss(a, b, t) smoothstep(a, b, t)
#define SS(U) smoothstep(3./R.y,0.,U)

float opU( float d1, float d2 ) { return  min(d1,d2); }
float opS( float d1, float d2 ) { return max(-d1,d2); }
float opI( float d1, float d2 ) { return max(d1,d2); }

vec2 opU2( vec2 d1, vec2 d2 ) 
{ if (d1.x<d2.x) return d1;   else return d2; }

vec2 opS2( vec2 d1, vec2 d2 )
{ if  (-d1.x<d2.x) return d1;  else  return d2;}

vec2 opI2( vec2 d1, vec2 d2 ) 
{ if (d1.x>d2.x) return d1; else return d2;}

vec2 opS(vec2 d1, vec2 d2) 
{ return vec2(max(-d2.x, d1.x), 2.0); }

vec2 opRep2D( in vec2 p, in vec2 c )
{    vec2 q = mod(p+0.5*c,c)-0.5*c; return  q; }

float sdCirc(vec2 p, float r)
{  return length(p)-r; }

float sdBox( in vec2 p, in vec2 b )
{ vec2 d = abs(p)-b;  return length(max(d,0.0)) + min(max(d.x,d.y),0.0); }


vec2 postesimple(vec2 uv)
{   float sbPoste1= sdBox(uv-vec2(0.5,-0.24), vec2(0.008,0.08) );
    vec2 resp=vec2(9999.9,-1.0);
    resp=opU2(resp, vec2( sbPoste1,2.0));
    return resp;
}


vec2 Pista(vec2 p)
{
   // p=Camino(p);
    vec2 c1=vec2(100.0,4.0);
    vec2 cpasto=vec2(10.0,1.0);
    vec2 pr1=opRep2D( p,c1);
    vec2 ppasto=opRep2D( p,cpasto);
    float sbPavimento= sdBox(pr1-vec2(0.0,0.0), vec2(1.0,2.5) );
    float sbPavimentoB= sdBox(pr1-vec2(0.0,0.0), vec2(22.0,2.5) );
    float sbIzq= sdBox(pr1-vec2(1.0,0.0), vec2(0.05,2.0) );
    float sbDer= sdBox(pr1-vec2(-1.0,0.0), vec2(0.05,2.0) );
    vec2 c2=vec2(10.0,2.0);
    vec2 pr2=opRep2D( p-vec2(0.0,iTime*2.0),c2);
    float sbCentro= sdBox(pr2-vec2(0.0,0.0), vec2(0.02,0.2) );
    vec2 resp=vec2(9999.9,-1.0);
    sbPavimentoB=opS(sbPavimento, sbPavimentoB);
    sbPavimento=opS(sbCentro,  sbPavimento);
    resp=opU2(resp, vec2( sbPavimentoB, 12.0));
    resp=opU2(resp, vec2( sbPavimento, 9.0));
    resp=opU2(resp, vec2( sbIzq, 2.0));
    resp=opU2(resp, vec2( sbDer, 2.0));
    resp=opU2(resp, vec2( sbCentro,1.0));


    return resp;

}


vec2 simpleVehiculo(vec2 p)
{
    float sb1= sdBox(p-vec2(0.0,-0.25), vec2(0.06,0.04) );
    float sLlantaIzq= sdBox(p-vec2(-0.08,-0.25), vec2(0.01,0.05) );
    float sLlantaDer= sdBox(p-vec2( 0.08,-0.25), vec2(0.01,0.05) );
    float sdVentanaA= sdHexagon( p-vec2(0.0,-0.2), 0.05 );
    float sdVentanaB= sdHexagon( p-vec2(0.0,-0.2), 0.06 );
    vec2 resp=vec2(9999.9,-1.0);
    sdVentanaA=opS(sdVentanaA,sdVentanaB);
    sdVentanaB=opS(sb1,sdVentanaB);
    resp=opU2(resp, vec2(sdVentanaB,4.0));
    resp=opU2(resp, vec2(sdVentanaA,1.0));
    resp=opU2(resp, vec2(sb1,3.0));
    resp=opU2(resp, vec2(sLlantaIzq,9.0));
    resp=opU2(resp, vec2(sLlantaDer,9.0));
    return resp;
}



float nubes(vec2 p)
{    
    vec2 centro=vec2(0.0,0.0);
    float c1= sdCirc(p-vec2(0.0,0.05), 0.105);
    float c2= sdCirc(p-vec2(0.15,0.02), 0.065);
    float c3= sdCirc(p-vec2(-0.15,0.02), 0.065);
    float b1=sdBox(p-vec2(0.0,-0.05), vec2(0.22,0.045 ) );
    float res=opU(c1, c2);
    res=opU(res, c3);
    res=opS(  b1,res );
    return res;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 pos = ( fragCoord.xy / iResolution.xy ) - vec2(0.5,0.5);	
    float horizon = 0.01;   //0.2
    float fov = 0.5; 
	float scaling = 0.1;

	vec3 p = vec3(pos.x, fov, pos.y - horizon);      
	vec2 s = vec2(p.x/p.z, p.y/p.z) * scaling;
    vec2 s2 = vec2(p.x/p.z, p.y/p.z)*0.75 ;

    vec2 sE1=simpleVehiculo(pos-vec2(0.25*sin(iTime*2.0),0.0) );
    vec2 sE2=simpleVehiculo(pos*0.5-vec2(0.25*sin(0.5+iTime*1.45),0.0) );
    vec2 sE3=simpleVehiculo(pos*2.0-vec2(0.135*sin(iTime*0.85),0.1) );

    vec3 col;
    vec2 ps1= postesimple(pos);
    float t1=iTime*2.0;
    vec2 p1=s2;
    p1.y-=t1;
    vec2 s1=Pista(p1);

    col= DibujoConBordeVec2(s, s1,col);
    col= DibujoConBordeVec2(s, sE1, col);
    col= DibujoConBordeVec2(s, sE2, col);
    col= DibujoConBordeVec2(s, sE3, col);
    col= DibujoConBordeVec2(s, ps1, col);


   if (s.y>0.0)
   {
        col = vec3(0.0,0.0,0.2+1.0*sin(iTime*0.5)); 
        float t1=mod(iTime, 5.0);
        vec2 mover=vec2(-1.5+t1,0.0);
        float n1= nubes(pos*2.0-vec2(0.0,0.25)+mover);
        float n2= nubes(pos*3.0-vec2(0.5,0.5)+mover);
        float n3= nubes(pos*4.0-vec2(1.5,1.5)+mover);

        col= DibujoConBordeVec2(pos, vec2(n1, 1.0), col);
        col= DibujoConBordeVec2(pos, vec2(n2, 1.0), col);
        col= DibujoConBordeVec2(pos, vec2(n3, 1.0), col);
   }    

    fragColor = vec4( col, 1.0 );
}

