void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 1.5 * (fragCoord.xy-0.5)/max(iResolution.x, iResolution.y);
    vec2 mse = 0.3 * iMouse.xy/iResolution.xy - 0.3;
    float speed = 20.0; 
    float t = iTime/100. * speed;

    //uv.x += uv.y * 0.4 * sin(t);    
    //uv.y += uv.x * 0.4 * cos(t);
	//uv.x += sin(t);

	for(float i=1.0;i<10.;i+=1.)
	{
		vec2 newUv=uv;
		newUv.x += mse.x/i*sin(i*i*uv.y+0.3*t)+1.0;
		newUv.y += mse.y/i*sin(i*i*uv.x+0.3*(t+10.))-1.4;
		uv=newUv;
	}
	vec3 col= vec3(0.5 + sin(3.*uv.x)*.5,
                   0.5 + sin(3.*uv.y)*.5,
                   0.5 + sin(3.*(uv.x+uv.y)) *.5);


	fragColor=vec4(col, 1.0);
}
