// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

// Origin : https://comic.naver.com/challenge/detail.nhn?titleId=734597&no=16 (by hwang-han)
// Used Template : https://www.shadertoy.com/view/3sSGWt (by BigWIngs)
// Thanks to FabriceNeyret2 for giving me some advices

#define V120        vec2(-1./2., sqrt(3.)/2.)
#define V30         vec2(sqrt(3.)/2., -1./2.)
#define TILE_WALL   vec4(.75, .44, .19, 1)
#define TILE_GROUND vec4(.95, .87, .66, 1)
#define S(v)        smoothstep(0.,10./R.y, v - .01)
#define HexDist(p)  max(dot(abs(p), normalize(vec2(1.,sqrt(3.)))), abs(p).x)
#define rand(n)     fract(sin(dot(n+vec2(12.3, 32.1), vec2(12.989, 4.1414))) * 123.321)

vec4 HexCoords(vec2 uv) {
    vec2 r = vec2(1, 1.73), 
         h = r*.5,
         a = mod(uv  , r ) -h,
         b = mod(uv-h, r ) -h,
        gv = dot(a,a) < dot(b,b) ? a : b;
    return vec4(gv, uv-gv);
}

void mainImage( out vec4 O,  vec2 u ) {
    vec2 R = iResolution.xy, 
         U = 5.*( 2.*u - R ) / R.y;
    U.y -= iTime;

    vec4 hc = HexCoords(U);
    float typ = rand(hc.zw), d,b;
    vec2 t = hc.xy; 

    if(typ < .8) {
        if(typ < .4) t.y *= -1.;
        d = .5 - t.y/length(t);
        t.x = abs(t.x);
        b = S(abs(dot(V120, t)));
        if(t.y < 0.) b *= S(t.x);
    }
    else {
        if(typ > .9) t.y *= -1.;
        d = abs(dot(V30, t)) - 1./sqrt(12.);
        b = S(abs(d));
    }
    O = d>0. ? TILE_WALL : TILE_GROUND; 
    O *= S(.5-HexDist(t)) *b;
}
