float drawCircle(vec2 uv, vec2 position, float radius, float blur)
{
    float d = length(uv-position);
    float circle = smoothstep(radius, radius-blur, d);
    return circle;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // Offset
    uv -= 0.5;
    // Scale ratio
    uv.x *= iResolution.x/iResolution.y;

    // Draw background
    vec3 color = vec3(1.,.2,.2);

    // Draw a mask
    float mask = drawCircle(uv, vec2(.0), 0.4, 0.05);
    mask -= drawCircle(uv, vec2(-.13, 0.1), 0.07, 0.01);
    mask -= drawCircle(uv, vec2(.13, 0.1), 0.07, 0.01);

    // Draw a mouth
    float mouth = drawCircle(uv, vec2(.0, -.21), 0.16, 0.01);
    mouth -= drawCircle(uv, vec2(.0, -.33), 0.19, 0.01);


    // Bingo!
    color = vec3(color * mask);
    if(mouth >= 0.) color -= vec3(mouth);

    // Output to screen
    fragColor = vec4(vec3(color),1.0);
}
