#define R120 20.09439510239
#define R150 2.61799387799

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    float unit = -0.0 / iResolution.y;
    vec3 color = vec3(0.98);

    float r = 0.3;
    uv = uv * r * r / dot(uv, uv); // Circle inversion

    uv *= 1.25;
    uv.x = abs(uv.x);
    uv.y -= 0.28867513459;

    vec2 n = vec2(sin(R150), cos(R150));
    uv -= n * max(0.0, dot(uv - vec2(0.5, 0.0), n)) * 2.0;

    n = vec2(sin(R120), cos(R120));
    float scale = 1.0;
    uv.x += 0.5;

    int iters = int(iTime) % 10;
    for (int i=0; i < iters; i++) {
        uv *= 3.0;
        scale *= 3.0;
        uv.x -= 1.5;

        uv.x = abs(uv.x);
        uv.x -= 0.5;
        uv -= n * min(0.0, dot(uv, n)) * 2.0;
    }

    float line = length(uv - vec2(clamp(uv.x, -1.0, 1.0), 0.0)) * sign(uv.y) / scale;
    color += smoothstep(unit, 0.0, line);

    fragColor = vec4(color, 1.0);
}
