void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;
	float X = uv.x*42.;
	float Y = uv.y*-32.;
	float t = iTime*3.6;
	float col = sin(sin(t+X/4.)-t+Y/9.+sin(X/(6.+sin(t*.1)+sin(X/9.+Y/9.))));

    fragColor =  fragColor = vec4( col / fwidth(col) );
}
