vec3 Circle(vec2 uv, vec2 p,float r, float blur, vec3 c){

    float d = length(uv - p);    
    d = smoothstep(blur, 0., d -r);


    vec3 col = vec3(d*c.r, d*c.g, d*c.b);
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;
    vec2 p = vec2(0., 0.);
    float offset = .125 * (sin(iTime)*.5+.5);
    vec3 c = Circle(uv, p, 0.3, 0.004, vec3(1., 0., 0.));
    c += Circle(uv, vec2(p.x - offset, p.y), 0.3, 0.004, vec3(0., 1., 0.));
    c += Circle(uv, vec2(p.x + offset, p.y), 0.3, 0.004, vec3(0., 0., 1.));

    // Output to screen
    fragColor = vec4(vec3(c), 1.0);
}
