void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 u = vec2(fragCoord -.5 * iResolution.xy) / iResolution.y;

    vec2 st = vec2(atan(u.x, u.y)*4., length(u));
    u = vec2(st.x/6.2831, st.y);

    vec2 mult = texture(iChannel0,vec2(0,u.y) * .0625).rg;
    u.x += (mult.r - .5) * iTime * .5;


    //float col = (fract(texture(iChannel0,u*.05).r)<.5?0.:1.); // parts robbed there : https://www.shadertoy.com/view/MsdfWn
    float col = (pow(texture(iChannel1, u).r,3.));






    fragColor = vec4(vec3(col),1.0);
}
