mat2 rot(float a) {
    float s=sin(a),c=cos(a);
    return mat2(c,-s,s,c);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv=-1.+2.*fragCoord/iResolution.xy;
    float duv=dot(uv,uv);
    uv*=.5+.5*duv;
    uv*=3.+sin(iTime*.123)*2.;
    for(int i=0;i<13;++i) {
        uv=abs(uv);
        uv*=rot(sin(iTime*.0234));
        uv-=.5;
        uv*=rot(iTime*.0664);
    }
    vec3 col=texture(iChannel0, uv).rgb;
    col*=1.-.3*duv;
    fragColor=vec4(col,1.);
}
