void mainImage(out vec4 color, vec2 coord) {
    vec2 ps = vec2(1.0) / iResolution.xy;
    vec2 uv = coord * ps;
    color = vec4(0.0, 0.0, 0.0, 1.0);

    vec2 mouse = mix(vec2(0.5), iMouse.xy * ps, step(0.01, iMouse.z));
    float center = mouse.x;
    float start = center - mouse.y;
    float end = center + mouse.y;

    color.r = (uv.y < 0.5)
        ? mix(0.0, 1.0, step(center, uv.x))
        : mix(0.0, 1.0, smoothstep(start, end, uv.x));
}
