float line_y = 0.25;


bool circle(vec2 uv, float r) {
    float len = length(uv - 0.5);
    return (
        (len > r) &&
        len < r + max(0.,iTime*speed-time_offset)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    if (circle(uv, 0.3))
        fragColor = texture(
            iChannel1, 
            uv - offset * (uv - 0.5)
        );
    else
        fragColor = texture(iChannel0, uv);        

}
