{
    vec4 black = vec4(0.0196, 0.0196, 0.0196, 1.0);
    vec4 white = vec4(1.0, 1.0, 1.0, 1.0);
    float divisions = 10000.0;

    float horizontalUV = fragCoord.x/iResolution.x;

    vec2 q = fragCoord.xy / iResolution.xy;
    vec2 p = q - 0.5;
    float gradient = clamp((p.y+0.42)*1.25,0.,1.);

    fragColor = vec4(mix(black, white, floor(horizontalUV * divisions) / divisions));

