//Nyquist–Shannon sampling theorem
//Sample rate less than Nyquist frequency ??!!
//Send Sinewave 100 to 2000Hz to audio input
//Edit line 12 and change default value less than 1 to see the effect

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;   
    float c=0.0;

    if (uv.y>0.0) {
        uv.y = (uv.y-.3)/0.5;
        uv.x /= 5.0;   //  yes here, choose range -5.0 to 0.95 

        // Audio Data INPUT from iChannel0
        c =  texture(iChannel0,vec2(uv.x,.5/2.)).r;
        c = (uv.y<c) ? 1. : 0.;

    }
     vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
     fragColor = vec4(c,col);
}
