void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    uv = abs(uv);
    vec2 mouse = iMouse.xy / iResolution.xy;
    vec2 mandleStride = vec2(0.0);

    const float maxDanceStep = 80.0;
    float iter = 0.0;
    vec2 offset = vec2(-.159995, 0.50999) * 2.0;
    vec2 zoomAmount = (uv * 0.25 * abs(max(0.05, cos(iTime * 0.02))));
    float swirlAmount = (1.0 + 0.1 * cos(iTime * 0.5));

    for(float i = 0.0; i < maxDanceStep; i++) {
        float horizontalPace = mandleStride.x * mandleStride.x;
        float verticalPace = mandleStride.y * mandleStride.y;
        mandleStride = vec2(horizontalPace - verticalPace, swirlAmount * mandleStride.x * mandleStride.y) + zoomAmount + offset;
        iter = i;
        if(length(mandleStride) > 5.0) break;
    }

    float aspect = pow(iter / maxDanceStep, 0.5);
    vec3 color = texture(iChannel0, vec2(aspect, sin(iTime * 0.003))).rgb;

    fragColor = vec4(color, 1.0);
}
