void mainImage( out vec4 d, in vec2 c )
{

  #define mix 1. // interference pattern
//  #define mix .5 // halftone pattern


  float x=tan((.0-iTime)/100.);
  float y=cos((.0-iTime)/8.);

  if (iMouse.x>=0.) {
   x = .001+(iMouse.x/iResolution.x)/10.;
   y =  iMouse.y/iResolution.y;
  } 

  float t = iTime/300.;

   vec4 a = vec4(
       sin( c.x*x + t ),
       sin( c.x*x *y*y*y     + t*y ),
       sin( c.x*x *y*y*y*y*y + t*y*y ),
       mix);

   vec4 b = vec4(
       sin( c.y*x + t ),
       sin( c.y*x *y*y*y     + t*y ),
       sin( c.y*x *y*y*y*y*y + t*y*y ),
       mix);

//   d = abs(a+b);
//   d = abs(a*b);
     d = abs(a);

}
