float m_BlurRadius = 5.0;
vec3 GetTexture(vec2 uv) {
    float boundarylimit =  step(0.0, uv.x) * step(0.0, uv.y) * step(uv.x, 1.0) * step(uv.y, 1.0);
    return texture(iChannel0, uv).rgb * boundarylimit;
}
vec3 GetXYDirectPixel(vec2 uv, float offset, float weight, float radius) {
    vec3 result = vec3(0.0);
    vec2 invSize = 1.0 / iResolution.xy;
    result += GetTexture((uv + vec2(offset, 0.0) * radius) * invSize) * weight;
    result += GetTexture((uv - vec2(offset, 0.0) * radius) * invSize) * weight;
    result += GetTexture((uv + vec2(0.0, offset) * radius) * invSize) * weight;
    result += GetTexture((uv - vec2(0.0, offset) * radius) * invSize) * weight;
    float de = 2.0 + step(offset, 0.0) * 2.0;
    return result / de;
}

vec3 Blur(vec2 fragCoord, float radius) {    
    float weights[5] = float[5](0.17620411, 0.28032472, 0.11089769, 0.01940710, 0.00126844);
    float offsets[5] = float[5](0.00000000, 1.42857143, 3.33333333, 5.23809524, 7.14285714);
    vec3 result = vec3(0.0);

    for (int i = 0; i < 5; ++i) {
        result += GetXYDirectPixel(fragCoord, offsets[i], weights[i], radius);
    }
    return result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec4 color = vec4(Blur(fragCoord.xy, m_BlurRadius), 1.0);
    fragColor = color; 
}
