bool isFragCoordInMouseArea(vec2 fragCoord) {
    //return (fragCoord.x <= iMouse.x + 50.0 && fragCoord.x >= iMouse.x - 50.0
    //    && fragCoord.y <= iMouse.y + 50.0 && fragCoord.y >= iMouse.y - 50.0);
    vec2 check = max (abs (fragCoord - iMouse.xy), vec2 (50.0));
    return (check == vec2 (50.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Mouse coords to uv coords
    //vec2 mouseCoord = iMouse.xy/iResolution.xy;

    // Time varying pixel color
    vec3 col1 = 0.5 + 0.5 * cos (iTime + uv.xyx + vec3 (0, 2, 4));
    vec3 col2 = 0.5 + 0.5 * sin (iTime + uv.xyx + vec3 (0, 2, 4));

    // Output to screen
    if (isFragCoordInMouseArea (fragCoord)) {
        fragColor = vec4 (col2, 1.0);
    } else {
        fragColor = vec4 (col1, 1.0);
    }
}
