bool mapX0(vec3 p, bool doOpenTop, bool doOpenTopFully)
{    
    bool r;

    r =      (p.x >= -8.0 && p.x < 8.0);
    r = r && (p.y >= -8.0 && p.y < 8.0);
    r = r && (p.z >= -8.0 && p.z < 8.0);      

    {
        bool a;
        a =      (p.x >= -7.0 && p.x < 7.0);
    	a = a && (p.y >= -7.0 && p.y < (doOpenTopFully ? 8.0 : 7.0));
    	a = a && (p.z >= -7.0 && p.z < 7.0); 

        r = r && !a;
    }

    {
        bool a;
        a =      (p.x >= -7.0 && p.x < 3.0);
    	a = a && (p.y >= -7.0 && p.y < 7.0);
    	a = a && (p.z == -3.0); 

        r = r || a;
    }

    {
        bool a;
        a =      (p.x >= -3.0 && p.x < 7.0);
    	a = a && (p.y >= -7.0 && p.y < 7.0);
    	a = a && (p.z == 2.0); 

        r = r || a;
    }

    {
        bool a;
        a =      (p.x >=  3.0 && p.x < 7.0);
    	a = a && (p.y >= -7.0 && p.y < 7.0);
    	a = a && (p.z == 7.0); 

        r = r && !a;
    }

    if(doOpenTop)
    {
        bool a;
        a =      (p.x >= -7.0 && p.x < 7.0);
    	a = a && (p.y == 7.0);
    	a = a && (p.z >= -7.0 && p.z < -3.0); 

        r = r && !a;
    }

    return r;
}

bool mapX(vec3 p, bool doOpenTopFully)
{
   //return mapX0(vec3(p.x, p.y, -p.z+7.0));
   return mapX0(vec3(p.x, p.y,  p.z+8.0), true,  doOpenTopFully) || 
          mapX0(vec3(p.x, p.y, -p.z+7.0), false, doOpenTopFully);
}

bool map0(vec3 p)
{
    p += 0.5;
    vec3 b = abs(p);

    bool r;

    r =      b.x < 4.0;
    r = r && b.y < 4.0;
    r = r && b.z < 4.0;

    r = r && (b.x > 2.0 || b.y > 2.0);
    r = r && (b.z > 2.0 || b.y > 2.0);
    r = r && (b.x > 2.0 || b.z > 2.0);    

    return r;
}

bool map1(vec3 p)
{
    float o = 2.0; 

    return map0(p + vec3( -o, 0.0, 0.0)) != 
           map0(p + vec3(  o, 0.0, 0.0)) != 
           map0(p + vec3(0.0,  -o, 0.0)) != 
           map0(p + vec3(0.0,   o, 0.0)) != 
           map0(p + vec3(0.0, 0.0,  -o)) != 
           map0(p + vec3(0.0, 0.0,   o));
}

#define map mapX

// slightly modified version of iq's DDA implementation: https://www.shadertoy.com/view/4dfGzs
bool VoxelRayCast(vec3 ro, vec3 rd, bool isPrimaryRay, /**/ out vec3 vp, out vec3 fm, out float t)
{
	vec3 pos = floor(ro);
	vec3 ri = 1.0/rd;
	vec3 rs = sign(rd);
	vec3 off = (-ro + (rs * 0.5 + 0.5)) * ri;

	vec3 mm = vec3(0.0);

	bool hit = false;
	for(int i = 0; i < 128; i++) 
	{
		if(map(pos, isPrimaryRay)) { hit = true; break; }

        vec3 dis = pos * ri + off;// re-comp dis every iter so we dont accu float errors
		mm = step(dis.xyz, dis.yzx) * (1.0 - step(dis.zxy, dis.xyz));
        pos += mm * rs;
	}

    // intersect the cube	
    vec3 mini = (pos - rs) * ri + off;   
	t = max(mini.x, max(mini.y, mini.z));

	fm = mm;
	vp = pos;

	return hit;
}


bool Intersect_Scene(vec3 rp, vec3 rd, bool isPrimaryRay, /**/ out float t, out vec3 n, out vec3 a)
{
    vec3 vp, fm;
    bool hit = VoxelRayCast(rp, rd, isPrimaryRay, /*out:*/ vp, fm, t);

    n = -fm * sign(rd);
    a = vec3(1.0);

    return hit;
}

const float BounceWeights[15] = float[15]
(
	1.0,
	1.0,
	1.8443,
	2.2499,
	2.0421,
	1.4709,
	0.8758,
	0.4433,
	0.1948,
	0.0754,
	0.0261,
