void mainImage(out vec4 outCol, in vec2 uv0)
{     
    vec2 uv = uv0.xy - 0.5;
	vec2 tex = uv0.xy / iResolution.xy;
    vec2 tex21 = tex * 2.0 - vec2(1.0);

    vec4 mouseAccu  = ReadVar4(1, 0);
    vec4 wasdAccu   = ReadVar4(2, 0);
    float frameAccu = ReadVar (3, 0);


    vec2 ang = vec2(-0. * Pi, -Pi * 0.5);
    ang += mouseAccu.xy * 0.008;

    mat3 cmat;
    {
        float sinPhi   = sin(ang.x);
        float cosPhi   = cos(ang.x);
        float sinTheta = sin(ang.y);
        float cosTheta = cos(ang.y);    

        vec3 front = vec3(cosPhi * cosTheta, 
                                   sinTheta, 
                          sinPhi * cosTheta);

        vec3 right = vec3(-sinPhi, 0.0, cosPhi);

        vec3 up    = vec3(-cosPhi * sinTheta,
                                    cosTheta,
                          -sinPhi * sinTheta);

        cmat = mat3(right, up, front);
    }

    float cdist = exp2(4.5 + mouseAccu.w * 0.02);
    vec3 cpos = -cmat[2] * cdist;


    float fId = frameAccu * 1.64683 + 0.84377;

    vec3 pxId  = vec3(frameAccu, uv); 
         pxId *= vec3( 0.76032, 1.47035, 0.92526); 
         pxId += vec3(-0.69060, 0.02293, 0.68109);

    uint hh = HashUInt(pxId, uvec3(0xB8D3E97Cu, 0x736D370Fu, 0xA7D00135u));

    vec2 tc;
    {
        vec2 off;
        {
            // filter kernel:
        	float h0 = Hash(fId, 0xAF609A13u);
        	float h1 = Hash(fId, 0xE0ABC868u);

        	//off = vec2(h0, h1) * 0.5;
        	off = vec2(Sample_Triangle(h0), Sample_Triangle(h1));
        }

        #if 0
        {
            // bloom kernel:
        	float h0 = HashFlt(hh, 0xE77CA8EFu);
        	float h1 = HashFlt(hh, 0x86AF71D2u);
        	h1 = clamp01(h1 * 0.5 + 0.5);

        	off += AngToVec(h0 * Pi) * Pow2(log2(1.0 - Pow2(h1))) * 0.25;
        }
        #endif

        tc = (uv0.xy + off - iResolution.xy * 0.5) / iResolution.xx;
    }

    vec3 lpos;
    {
        // lens pos:
        float h0 = HashFlt(hh, 0x27BB116Bu);
        float h1 = HashFlt(hh, 0x11A95B42u);
		h1 = clamp01(h1 * 0.5 + 0.5);

        vec2 lpos0 = Sample_Disk(h0, h1) * 0.0;

        lpos = cmat * vec3(lpos0, 0.0);
    }


    float focalLen = 0.4;// 0.5 * tan(Pi05 - fov * 0.5)
    float S1 = max(18.0, cdist);// focus plane dist / focalLen
    vec3 rdir = normalize(cmat * (vec3(tc, focalLen) * S1) - lpos); 


    vec2 lightAng = vec2(Pi * 0.7, Pi * 0.1);
    lightAng.x += (wasdAccu.y - wasdAccu.w) * 0.06; 
    lightAng.y += (wasdAccu.x - wasdAccu.z) * 0.04;    

    vec3 light0 = AngToVec(lightAng);
    light0 = vec3(0.49292178644304296, 0.7169771439171534, 0.49292178644304296);

    vec3 light = light0;
    {
        float h0 = HashFlt(hh, 0x9E2355B4u);
        float h1 = HashFlt(hh, 0xDC305E12u);
        h1 = clamp01(h1 * 0.5 + 0.5);

        h1 = mix(0.999, 1.0, h1);
        h1 = 1.0;

        light = Sample_Sphere(h0, h1, light);
    }


    vec3 pot = vec3(1.0);
    vec3 col = vec3(0.0);

    float t; vec3 n; vec3 a;
    vec3 pos = cpos + lpos;
    vec3 dir = rdir;

    vec2 tt; 
    float res = Intersect_Ray_Cube(pos, dir, vec3(8.0 + 1e-5, 8.0 + 1e-5, 16.0 + 1e-5), /*out:*/ tt);   

    if(res == 1.0)
    {
    	pos += dir * tt.x;
    }

    if(res != -1.0)
    {
    for(uint i = 0u; i < 15u; ++i)//14u
    {

    if(Intersect_Scene(pos, dir, i == 0u, /*out:*/ t, n, a))
    {
        vec3 p = pos + dir * t;

		a = vec3(1.0);
		//a = vec3(0.75);
        if(false)
        {
            vec3 fp = fract(p);
        	float s = 0.02;
            bool m;
        	m =      (fp.x < s || fp.x > 1.0 - s || fp.y < s || fp.y > 1.0 - s);   
        	m = m && (fp.x < s || fp.x > 1.0 - s || fp.z < s || fp.z > 1.0 - s);   
        	m = m && (fp.z < s || fp.z > 1.0 - s || fp.y < s || fp.y > 1.0 - s);   

            a *= m ? 0.0 : 1.0;
        }

        //a *= (fp.y < s || fp.y > 1.0 - s ? 0.0 : 1.0);
        //a *= (fract(p.z) < 0.05 || fract(p.z) > 1.0 - 0.05 ? 0.0 : 1.0);

        pot *= a;

        float sh;
        {
            float t2; vec3 n2; vec3 a2;
            sh = Intersect_Scene(p + n * 0.0001, light, false, /*out:*/ t2, n2, a2) ? 0.0 : 1.0;
        }

        col += pot * clamp01(dot(light, n)) * sh * BounceWeights[i];// * RcpPi;// impl scale dir light E by Pi

        pos = p + n * 0.0001;
        {
            float h0 = HashFlt(hh, 0x874C40D4u);
            float h1 = HashFlt(hh, 0xF27BD7E1u);

            #if 0
            dir = normalize(Sample_Sphere(h0, h1) + n);
            #else
            h1 = clamp01(h1 * 0.5 + 0.5);
        	dir = Sample_ClampedCosineLobe(h0, h1, n);
            #endif
        }

    } 
    else 
    {
        //col += pot * (textureLod(iChannel2, dir, 0.0).rgb);
        //col += pot * BounceWeights[i];

        //if(i == 0u && dot(light0, dir) > 0.999) col += vec3(Pi);

        break;
    }

        hh = Hash(hh);
    }
    } 
    else 
    {
       // col += pot * (textureLod(iChannel2, dir, 0.0).rgb);

        if(dot(light0, dir) > 0.999) col += vec3(Pi);      
    }

    vec3 colLast = textureLod(iChannel0, tex, 0.0).rgb;

    col = mix(colLast, col, 1.0 / (frameAccu + 1.0));    

    outCol = vec4(col, 0.0);


    {
        // persistent state stuff:
        vec4 iMouseLast     = ReadVar4(0, 0);
        vec4 iMouseAccuLast = ReadVar4(1, 0);
        vec4 wasdAccuLast   = ReadVar4(2, 0);
        float frameAccuLast = ReadVar (3, 0);


        bool shift = ReadKey(KEY_SHIFT) != 0.0;

        float kW = ReadKey(KEY_W);
        float kA = ReadKey(KEY_A);
        float kS = ReadKey(KEY_S);
        float kD = ReadKey(KEY_D);

        float left  = ReadKey(KEY_LEFT);
        float right = ReadKey(KEY_RIGHT);
        float up    = ReadKey(KEY_UP);
        float down  = ReadKey(KEY_DOWN);


        bool anyK = false;

        anyK = anyK || iMouse.z > 0.0;
        anyK = anyK || shift;
        anyK = anyK || kW != 0.0;
        anyK = anyK || kA != 0.0;
        anyK = anyK || kS != 0.0;
        anyK = anyK || kD != 0.0;
        anyK = anyK || left  != 0.0;
        anyK = anyK || right != 0.0;
        anyK = anyK || up    != 0.0;
        anyK = anyK || down  != 0.0;


        frameAccuLast += 1.0;
        if(anyK) frameAccuLast = 0.0;


        vec4 wasdAccu = wasdAccuLast;
        wasdAccu += vec4(kW, kA, kS, kD);
        wasdAccu += vec4(up, left, down, right);        


        vec2 mouseDelta = iMouse.xy - iMouseLast.xy;

        bool cond0 = iMouse.z > 0.0 && iMouseLast.z > 0.0;
        vec2 mouseDelta2 = cond0 && !shift ? mouseDelta.xy : vec2(0.0);
        vec2 mouseDelta3 = cond0 &&  shift ? mouseDelta.xy : vec2(0.0);

        vec4 iMouseAccu = iMouseAccuLast + vec4(mouseDelta2, mouseDelta3);


        WriteVar4(iMouse,        0, 0);
        WriteVar4(iMouseAccu,    1, 0);
        WriteVar4(wasdAccu,      2, 0);
        WriteVar (frameAccuLast, 3, 0);
    }
}

