float checkCircle(vec2 center, vec2 pos, float radius, float thickness, float stepRadius)
{
    float len = length(pos - center);
    float distMod = mod(len, stepRadius+thickness);
    return smoothstep(1.5, 0.0, abs(distMod - radius));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float maxRadius = 20.0;
    float radius = max( maxRadius * fract(iTime), 1.0);

    vec3 col = smoothstep( vec3(1.0), vec3(0.0), vec3(checkCircle( iResolution.xy * 0.5, fragCoord.xy, radius, 1.0, maxRadius)));
    //col = mix(col, vec3(0.0), checkCircle( iResolution.xy * 0.25, fragCoord.xy, radius * 0.5, 1.0));

    // Output to screen
    fragColor = vec4(col,1.0);
}
