void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 stretchUV  = uv/16.0;
    float slowTime = iTime/32.0;

    float noise = texture(iChannel0,stretchUV+slowTime).x;

    float offset = (noise/4.0) - 0.125;

    float lineWidth = 0.015;

    // Time varying pixel color
    float col = float(uv.x > ((0.5 - lineWidth)+offset)) * float(uv.x < ((0.5 + lineWidth)+offset));

    vec3 color = vec3(col);

    // Output to screen
    fragColor = vec4(color,1.0);
}
