// golfed/simplified version of https://shadertoy.com/view/4llBRl

void mainImage(out vec4 O, vec2 u) {
    vec2 R = iResolution.xy;
    for (float L = 25e2, l, x = 0.; x<=5.; x += .005 )
        l  = L* length( (u+u-R)/R.y  - vec2( x-1.2, .2*sin(x+x-iTime)) ),
        O -= cos( sqrt(.3/l) *x*L ) / l * exp((L-l)*(l-L)/5e6);
    O = .5 + O*5.;
}
