void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p=(fragCoord.xy*2.0-iResolution.xy)/iResolution.y;

    int j=0;
    vec2 x=p+vec2(-0.7,0.0);
    vec2 z=vec2(0.0,0.0);

    for(int i=0;i<360;i++)
    {
    	j++;
        if(dot(z,z)>2.){break;}
        z=mat2(z,-z.y,z.x)*z+x*1.1;

    }

    float t=float(j)/360.;
    fragColor=vec4(t);
}
