vec4 sca(sampler2D tex, vec2 uv, float width)
{
    vec3 acc = vec3(0);

    const float delta = 0.04;
    const float range = acos(-1.0);
    const float spec = 2.0 * range / 3.0;

    for(float i = -1.0; i <= 1.0; i += delta)
    {
        vec3 mask = 0.5 + 0.5 * cos(range * i + vec3(-spec, 0, spec));
        acc += mask * texture(tex, uv - vec2(width * i, width * i)).rgb;
    }

    return vec4(delta * acc, 0);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    fragColor = sca(iChannel0, uv, 0.01*uv.x*uv.x+0.05);
}
