float line(vec2 uv, float height, float width) 
{
    return smoothstep(height-width/2., height, uv.y) // Add what's above the line's bottom
         - smoothstep(height, height+width/2., uv.y); // Subtract what's above the line's top
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/min(iResolution.x, iResolution.y);

    vec3 col = vec3(line(uv, .5+sin(iTime)*.5, .02), 0, 0);

    // Output to screen
    fragColor = vec4(col,1.0);
}
