#ifdef GL_ES
precision mediump float;
#endif

vec4 getColor(vec2 coord) {
    return texture(iChannel0, coord / iResolution.xy).rgba;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	if (fragCoord.y > iMouse.y)
	{
		fragColor = getColor(fragCoord.xy);	
	} else {
		int r = max(1, int(30. * (iMouse.y - fragCoord.y)/iResolution.y));
        int summ = 0;
		vec4 final_colour = vec4(0.);

		for (int i=-r; i <= r; ++i)
		{
			for (int j=-r; j <= r; ++j)
			{
                int cof2 = (r-i)*(r-i) + (r-j)*(r-j);
                summ += cof2;
				final_colour += getColor(fragCoord.xy + vec2(i,j))*float(cof2);
			}
		}		

		fragColor = final_colour/float(summ);
	}
}
