void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.xy;

    float pct = 0.0;
    int mul = 1;
    float vary = abs(tan(iTime/10.0));
    for(int i = 0; i < 30; i++) {
        vec4 rect = step(vec4(float(i)*vary/30.0), 

                vec4(st.x, st.y, (1.0-st).x, (1.0-st).y));
        float p = rect.x * rect.y * rect.z * rect.w;
        pct += float(mul) * p;
        mul *= -1;
    }

    vec3 color = vec3(pct);
    fragColor = vec4(color, 1.0);
}
