void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float density=3.1415926*8.;//controls how small the checkers are

    //normalize stuff
    vec2 uv = fragCoord/(.5*iResolution.y)-vec2(iResolution.x/iResolution.y,1.);

    //transform space
    vec2 t=uv/(dot(uv,uv));
    t+=vec2(sin(iTime*0.2),cos(iTime*0.2));

    //checkerboard pattern
    float col=smoothstep(-0.31,0.31,cos(t.x*density)+cos(t.y*density));

    //white light in the middle (remove to see the original ugliness)
    col=col+.05/dot(uv,uv);

    // Output to screen
    fragColor = vec4(vec3(col),1.0);
}
