const int RAY_STEPS = 256;
const vec3 lightPos = vec3(0., 7.45, -1.5);
const vec3 eyePos = vec3(0.0, 4.5, -20.0);
const vec3 refPos = vec3(0., 2.5, 0.);
const float PI = 3.14159265359;
const float FOVY = 19.5 * PI / 180.;
const float AO_DIST = 0.15;
const float FIVETAP_K = 2.0;

struct Intersection
{
    float t;
    vec3 color;
    vec3 p;
    int object;
};

/***************** BASIC SCENE SET UP *******************/

#define FLOOR_SDF plane(pos, vec4(0, 1, 0, 2.5));
#define RED_WALL_SDF plane(pos, vec4(1, 0, 0, 5));
#define GREEN_WALL_SDF plane(pos, vec4(-1, 0, 0, 5));
#define BACK_WALL_SDF plane(pos, vec4(0, 0, -1, 5));
#define CEILING_SDF plane(pos, vec4(0, -1, 0, 7.5));
#define TALL_BOX_SDF box(rotateY(pos + vec3(2, 0, -3), 27.5 * PI / 180.), vec3(1.5, 3., 1.5));
#define SMALL_BOX_SDF box(rotateY(pos + vec3(-2, 1, -0.75), -17.5 * PI / 180.), vec3(1.5));

const int FLOOR_ID = 0;
const int RED_WALL_ID = 1;
const int GREEN_WALL_ID = 2;
const int BACK_WALL_ID = 3;
const int CEILING_ID = 4;
const int TALL_BOX_ID = 5;
const int SMALL_BOX_ID = 6;

vec3 rotateY(vec3 p, float a) {
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);
}

// Box with side lengths b
float box(vec3 p, vec3 b)
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float plane( vec3 p, vec4 n )
{
  // n must be normalized
  return dot(p,n.xyz) + n.w;
}

float sceneMap3D(vec3 pos)
{
    float flr = FLOOR_SDF;
    float red_wall = RED_WALL_SDF;
    float green_wall = GREEN_WALL_SDF;
    float back_wall = BACK_WALL_SDF;
    float ceiling = CEILING_SDF;
    float tall_box = TALL_BOX_SDF;
    float small_box = SMALL_BOX_SDF;
    return min(back_wall, min(red_wall, min(green_wall, 
        min(ceiling, min(flr, min(tall_box, small_box))))));
}

void sceneMap3D(vec3 pos, out float t, out int obj) {
    t = FLOOR_SDF;
    obj = FLOOR_ID;

    float t2 = RED_WALL_SDF
    if (t2 < t) {
        t = t2;
        obj = RED_WALL_ID;
    } 
    t2 = GREEN_WALL_SDF;
    if (t2 < t) {
        t = t2;
        obj = GREEN_WALL_ID;
    }
    t2 = BACK_WALL_SDF;
    if (t2 < t) {
        t = t2;
        obj = BACK_WALL_ID;
    }
    t2 = CEILING_SDF;
    if (t2 < t) {
        t = t2;
        obj = CEILING_ID;
    }
    t2 = TALL_BOX_SDF;
    if (t2 < t) {
        t = t2;
        obj = TALL_BOX_ID;
    }
    t2 = SMALL_BOX_SDF
    if (t2 < t) {
        t = t2;
        obj = SMALL_BOX_ID;
    }
}

/***************** RAY MARCHING LOGIC *******************/

vec3 rayCast(vec3 eye, vec3 ref, vec2 uv) {

    float len = tan(FOVY) * distance(eye, ref);
    vec3 H = normalize(cross(vec3(0.0, 1.0, 0.0), ref - eye));
    vec3 V = normalize(cross(H, eye - ref));
    V *= len;
    H *= len * iResolution.x / iResolution.y;
    vec3 p = ref + uv.x * H + uv.y * V;
    return normalize(p - eye);
}

void march(vec3 origin, vec3 dir, out float t, out int hitObj)
{
    t = 0.001;
    for(int i = 0; i < RAY_STEPS; ++i)
    {
        vec3 pos = origin + t * dir;
        float m;
        sceneMap3D(pos, m, hitObj);
        if(m < 0.01) { return; }
        t += m;
    }
    t = -1.0;
    hitObj = -1;
}


/***************** EFFECTS *******************/

float fiveTapAO(vec3 p, vec3 n, float k) {
    float aoSum = 0.0;
    for(float i = 0.0; i < 5.0; ++i) {
        float coeff = 1.0 / pow(2.0, i);
        aoSum += coeff * (i * AO_DIST - sceneMap3D(p + n * i * AO_DIST));
    }
    return 1.0 - k * aoSum;
}

/***************** MATERIALS *******************/

vec3 computeNormal(vec3 pos)
{
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize( vec3( sceneMap3D(pos + epsilon.yxx) - sceneMap3D(pos - epsilon.yxx),
                            sceneMap3D(pos + epsilon.xyx) - sceneMap3D(pos - epsilon.xyx),
                            sceneMap3D(pos + epsilon.xxy) - sceneMap3D(pos - epsilon.xxy)));
}

vec3 computeMaterial(int hitObj, vec3 p, vec3 nor, vec3 lightDir, vec3 view) {
    float lambert = max(0., dot(lightDir, nor));

    if (hitObj == 0) { return vec3(0.85, 0.81, 0.78) * lambert; }
    if (hitObj == 1) { return vec3(0.63, 0.065, 0.05) * lambert; }
    if (hitObj == 2) { return vec3(0.14, 0.45, 0.091) * lambert; }
    if (hitObj == 3) { return vec3(0.85, 0.81, 0.78) * lambert; }
    if (hitObj == 4) { return vec3(0.85, 0.81, 0.78) * lambert; }
    if (hitObj == 5) { return vec3(0.85, 0.81, 0.78) * lambert; }
    if (hitObj == 6) { return vec3(0.85, 0.81, 0.78) * lambert; }

    else { return vec3(0., 0., 1) * lambert; }
}

/***************** BASIC FUNCTIONALITY *******************/

Intersection sdf3D(vec3 dir, vec3 eye)
{
    float t;
    int hitObj;
    march(eye, dir, t, hitObj);

    vec3 isect = eye + t * dir;
    vec3 nor = normalize(computeNormal(isect));
    vec3 lightDir = normalize(lightPos - isect);

    //AO logic
    float ao = fiveTapAO(isect, nor, FIVETAP_K);
    vec3 surfaceColor = computeMaterial(hitObj, isect, nor, lightDir, normalize(eye - isect));
    surfaceColor = ao * surfaceColor;

    return Intersection(t, surfaceColor, isect, hitObj);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // [-1, 1]
    uv = 2.0 * uv - vec2(1.0);

    vec3 eye = eyePos;
    vec3 ref = refPos;

    vec3 dir = rayCast(eye, ref, uv);

  	Intersection isect = sdf3D(dir, eye);

    fragColor = vec4(isect.color, 1.0);  
}
