#define NUM 8

float GatherSoundInfo(int SampleWished)// 0 - 512
{
    return texelFetch(iChannel0, ivec2(float(SampleWished), 0), 0).r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = fragCoord/iResolution.xy*10.;
	float w = 450./iResolution.x;
    vec3 c = vec3(0.);
    float s;

    for(int i = 1; i<NUM+1; i++)
    {
    s = GatherSoundInfo((512/NUM)*i-(512/NUM)/2);
    vec3 col = vec3(1.0,float(i)/float(NUM),0.0);
    c += smoothstep(w,-w,vec3(distance(uv.y +sin(uv.x*s+2.0)*1.3,0.9*float(i+1))))*col;
    } 
    fragColor = vec4(c, 1.0);
}
