#define PI 3.141592
#define sequencer iTime

vec3 Cross(vec2 uv) {
      float d = length(floor(uv*10.))-.9;
   d = smoothstep(0.42,0.43,d);
   return vec3((1.-d),0.,0.);
}



vec3 Circle(vec2 uv) {

    float a = 0.27;
    float d = smoothstep(a,a+0.01,length(uv));
    return vec3(0.,0.,(1.-d)*.9);
}

vec3 Object1(vec2 uv) {
    vec3 cr = Cross(uv+.05);
    vec3 ci = Circle(uv);
    cr.r == 1. ? ci.b=0. : ci.x;
    return cr+ci;
}
vec3 ObjectRed(vec2 uv, float offset) {  
    uv*= smoothstep(0.0,0.5,max(0.,sin(sequencer-.5+offset)))+1.5;
    return Object1(uv).bgr;
}

vec3 ObjectBlue(vec2 uv, float offset) {  
    uv*= smoothstep(0.0,0.5,max(0.,-sin(sequencer+offset)))+1.5;
    return Object1(uv);
}
mat2 r(float a){return mat2(cos(a),sin(a),-sin(a),cos(a));}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
 vec3 col =vec3(0);

    float q = floor((uv.x+.25) /.5);
    uv.x = mod(uv.x+.25,.5)-.25;
     float offset = 0.;

    if(cos(iTime) < 0.) {
        if(mod(q,2.) == 0.) {
            col = ObjectRed(uv,0.) ; 
        }
        else {
             col = ObjectBlue(uv , 0.) ;
        } 
    } else {  
        if(mod(q,2.) == 0.) {
            col = ObjectBlue(uv, PI ) ; 
        }
        else {
             col = ObjectRed(uv , PI ) ;
        } 
    }



    fragColor = vec4(col,1.0);
}
