// Cole Peterson
#define R iResolution

void mainImage( out vec4 f, in vec2 u )
{
    vec2 uv = vec2(u.xy - 0.5*R.xy)/R.y;

    vec3 col = vec3(0);

    float r = .5;
    float n = 20.;

    for(float i = 0.; i < n; i++)
    {
        uv.y += sin(i*0.5 + iTime*4.)*0.01;
        uv.x += cos(i*0.5 + iTime*4.)*0.01;

        vec2 ruv = fract(uv*2.)-.5;
        vec2 id = floor(uv*2.);

        vec3 nc = .8+.5*cos(vec3(2.,3.,0.4)*(id.x+id.y+i*48.)*4.);
        nc *= (i+0.7) / n;

    	col = mix(col, nc, smoothstep(r, r - .015, length(ruv)));

        uv *= 1.04;
        r -= 0.023;
    }
    col *= ((1.-abs(uv.x*.3)) * (1.-abs(uv.y*.3)))*1.1;
    f = vec4(col,1.0);
}
