float ease( float x ) {                                 // rewritten from https://shadertoy.com/view/3ddBWB
	return x > .5 ? x = 1.-x, 1. - 4.* x*x*x            // stepier than smoothstep or .5-.5*cos
                  :                4.* x*x*x ;
}

#define D \
    V = abs( ( fract(U/2.)-.5)*mat2( cos(3.14/2.*t + vec4(0,11,33,0))) ); \
    O += smoothstep( w, -w, max(V.x,V.y) - .25 ) 

void mainImage( out vec4 O, vec2 u )
{   
    O-=O; 
    vec2 R = iResolution.xy,
         U = u *= 8. / R.y, V;
    if (U.x > 8.) { O += .5; return; }                  // out of board

    float t = ease( fract(iTime/2.) ), w = 4./R.y,
          n = mod(iTime/2.,2.);

    if (n > 1.)  U.x++, t = 1.-t;
    U -= .5; D;
    U++;     D;
    if (n < 1.)  O = 1.-O;
}
